<script>
    let dPwd = false;

    function vPwd() {
        let gPwd = $("#pwd");
        if (dPwd === false) {
            $("#eye").removeClass("fa-eye-slash")
            $("#eye").addClass("fa-eye")
            gPwd.attr("type", "text");
            dPwd = true;
        } else if (dPwd === true) {
            $("#eye").removeClass("fa-eye")
            $("#eye").addClass("fa-eye-slash")
            gPwd.attr("type", "password");
            dPwd = false;
        }
    }

</script>
